import FlexBetween from "../../components/FlexBetween";
import UserImage from "../../components/UserImage";
import WidgetWrapper from "../../components/WidgetWrapper";
import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { setPosts } from "../../state/index";
import PostWidget from "../widgets/PostWidget";
import { Typography } from "@mui/material";

const PostsWidget = ({ userId, isProfile = false }) => {
  const dispatch = useDispatch();
  const posts = useSelector((state) => state.posts);
  const token = useSelector((state) => state.token);
  console.log(token);

  // GET ALL POSTS FOR HOMEPAGE
  const getPosts = async () => {
    try {
      const response = await fetch(`http://localhost:3001/posts`, {
        method: "GET",
        header: { Authorization: `Bearer ${token}` }, //authentication
      });
      const data = await response.json();
      dispatch(setPosts({ posts: data })); //remember the post array is set as "Action.payload.posts"
    } catch (error) {
      {
        error.message;
      }
    }
  };

  // GET PROFILE POSTS ONLY
  const getUserPosts = async () => {
    const response = await fetch(
      `http://localhost:3001/posts/${userId}/posts`,
      {
        method: "GET",
        header: { Authorization: `Bearer ${token}` }, //authentication
      }
    );
    const data = await response.json();
    dispatch(setPosts({ posts: data })); //remember the post array is set as "Action.payload.posts"
  };

  useEffect(() => {
    if (isProfile) {
      getUserPosts();
    } else {
      getPosts();
    }
  }, [isProfile]); //eslint-disable-line react-hooks/exhaustive-deps

  return (
    <>
      {posts.map(
        ({
          _id,
          userId,
          firstName,
          lastName,
          description,
          location,
          picturePath,
          userPicturePath,
          likes,
          comments,
        }) => (
          <PostWidget
            key={_id}
            postId={_id}
            postUserId={userId}
            name={`${firstName} ${lastName}`}
            description={description}
            location={location}
            picturePath={picturePath}
            userPicturePath={userPicturePath}
            likes={likes}
            comments={comments}
          />
        )
      )}
    </>
  );
};

export default PostsWidget;
