import React, { useEffect } from "react";
import { Box, useMediaQuery } from "@mui/material";
import Navbar from "../navbar/index";
import { useSelector } from "react-redux";
import UserWidget from "../widgets/UserWidgets.jsx";
import MyPostWidget from "../../scenes/widgets/MyPostWidget";
import PostsWidget from "../../scenes/widgets/PostsWidget";
import AdvertWidget from "../../scenes/widgets/AdvertWidgets";
import FriendListWidget from "../../scenes/widgets/FriendListWidget";
import { useParams } from "react-router-dom";

const ProfilePage = () => {
  const isNonMobileScreens = useMediaQuery("(min-width: 1000px)");
  console.log(userId);
  // const { _id, picturePath } = useSelector((state) => state.user);

  useEffect(() => {
    let { userId } = useParams();
    const friends = useSelector((state) => state.user.friends);
    const friend = friends.filter((friend) => friend._id === userId);
  }, []);

  console.log(friend);
  // const { _id, picturePath } = friend;
  const _id = friend._id;
  console.log(_id);

  return (
    <Box>
      <Navbar />
      <Box
        width="100%"
        padding="2rem"
        display={isNonMobileScreens ? "flex" : "block"} //on a computer youll have 3 widgets in a row, on phone is 1 widget a row
        gap="0.5rem"
        justifyContent="space-between"
      >
        <Box flexBasis={isNonMobileScreens ? "26%" : undefined}>
          <UserWidget userId={friend._id} picturePath={friend.picturePath} />
        </Box>
        <Box
          flexBasis={isNonMobileScreens ? "42%" : undefined}
          mt={isNonMobileScreens ? undefined : "2rem"}
        >
          {/* <MyPostWidget picturePath={picturePath} /> */}
          <PostsWidget userId={friend._id} isProfile="true" />
        </Box>
        {/* {isNonMobileScreens && (
          <Box flexBasis="26%">
            <AdvertWidget />
            <Box m="2rem 0" />
            <FriendListWidget userId={_id} />
          </Box>
        )} */}
      </Box>
    </Box>
  );
};

export default ProfilePage;
