import {
  EditOutlined,
  DeleteOutlined,
  AttachFileOutlined,
  GifBoxOutlined,
  ImageOutlined,
  MicOutlined,
  MoreHorizOutlined,
} from "@mui/icons-material";
import {
  Box,
  Divider,
  Typography,
  InputBase,
  useTheme,
  Button,
  IconButton,
  useMediaQuery,
} from "@mui/material";
import Dropzone from "react-dropzone";
import FlexBetween from "../../components/FlexBetween";
import UserImage from "../../components/UserImage";
import WidgetWrapper from "../../components/WidgetWrapper";
import { useDispatch, useSelector } from "react-redux";
import { useState } from "react";
import { setPosts } from "../../state/index";

const MyPostWidget = () => {
  const dispatch = useDispatch();
  const [isImage, setIsImage] = useState(false); //BOOLEAN: if User opens the image box (to upload an image)
  const [image, setImage] = useState(null); //image file
  const [post, setPost] = useState("");
  const { palette } = useTheme();
  const { _id, picturePath } = useSelector((state) => state.user);
  const token = useSelector((state) => state.token); //checking authorization
  const isNonMobileScreens = useMediaQuery(" (min-width: 1000px) ");
  const mediumMain = palette.neutral.mediumMain;
  const medium = palette.neutral.medium;

  const handlePost = async () => {
    //Here's the logic when creating a new Post
    const formData = new FormData(); //we are CREATING a new Form, and passing in the image
    formData.append("userId", _id);
    formData.append("description", post);
    if (image) {
      //IF an image exists, THEN
      formData.append("picture", image); //REMINDER: we MUST label watever we want formData to upload, as "picture". This was set in server/index.js.
      formData.append("picturePath", image.name); //REMINDER: picturePath is the images url path, image name
    }

    // Pass the new post to the backend to save along with all data
    const response = await fetch(`http://localhost:3001/posts`, {
      method: "POST",
      headers: { Authorization: `Bearer ${token}` }, //authentication
      body: formData, //PASS over the form we just created
    });

    // receive the fetch's response & save into 'posts' --> set the global state of 'Posts' with the 'posts'
    // reset useStates
    const posts = await response.json();
    dispatch(setPosts({ posts })); //load this updated posts into the global state
    setImage(null);
    setPost("");
  };

  return (
    <WidgetWrapper>
      <FlexBetween gap="1.5rem">
        <UserImage image={picturePath} />
        <InputBase
          placeholder="what's on your mind?"
          onChange={(e) => setPost(e.target.value)} //updating this post after edits
          value={post}
          sx={{
            width: "100%",
            backgroundColor: palette.neutral.light,
            borderRadius: "2rem",
            p: "1rem 2rem",
          }}
        />
      </FlexBetween>
      {isImage && (
        <Box
          border={`1px solid ${medium}`}
          borderRadius="5px"
          padding="1rem 2rem"
          mt="1rem"
          p="1rem"
        >
          <Dropzone
            acceptedFiles=".jpg,.jpeg,.png,"
            multiple={false}
            onDrop={
              (acceptedFiles) => setImage(acceptedFiles[0]) //dropzone is a third party app, so we need to set this field's value manually, UNLKE the inputs before this one that are programmed with react.
            }
          >
            {({ getRootProps, getInputProps }) => (
              <FlexBetween>
                <Box
                  {...getRootProps()} //MUST do with dropzone app. MUST pass the arguments into the immediate div underneath that the dropzone app with be in
                  border={`2px dashed ${palette.primary.main}`}
                  p="1rem"
                  width="100%"
                  sx={{ "&:hover": { cursor: "pointer" } }} //on hover MUI condition
                >
                  <input {...getInputProps()} />
                  {!image ? (
                    // picture upload widget
                    <p>Add Image here</p>
                  ) : (
                    <FlexBetween>
                      <Typography>{image.name}</Typography>
                      <EditOutlined />
                    </FlexBetween>
                  )}
                </Box>
                {image && (
                  <IconButton
                    onClick={() => {
                      setImage(null);
                    }}
                    sx={{
                      width: "15%",
                    }}
                  >
                    <DeleteOutlined />
                  </IconButton>
                )}
              </FlexBetween>
            )}
          </Dropzone>
        </Box>
      )}

      <Divider
        sx={{
          margin: "1.25rem 0",
        }}
      />
      <FlexBetween>
        <FlexBetween gap="0.25rem" onClick={() => setIsImage(!isImage)}>
          <ImageOutlined sx={{ color: mediumMain }} />
          <Typography
            color={mediumMain}
            sx={{
              ":&hover": { cursor: "pointer", color: medium },
            }}
          >
            Image
          </Typography>
        </FlexBetween>
        {isNonMobileScreens ? (
          <>
            <FlexBetween gap="0.25rem">
              <GifBoxOutlined
                sx={{
                  color: mediumMain,
                }}
              />
              <Typography color={mediumMain}>Clip</Typography>
            </FlexBetween>

            <FlexBetween gap="0.25rem">
              <AttachFileOutlined
                sx={{
                  color: mediumMain,
                }}
              />
              <Typography color={mediumMain}>Attachment</Typography>
            </FlexBetween>

            <FlexBetween gap="0.25rem">
              <MicOutlined
                sx={{
                  color: mediumMain,
                }}
              />
              <Typography color={mediumMain}>Audio</Typography>
            </FlexBetween>
          </>
        ) : (
          <FlexBetween gap="0.25rem">
            <MoreHorizOutlined sx={{ color: mediumMain }} />
          </FlexBetween>
        )}

        <Button
          disabled={!post}
          onClick={handlePost}
          sx={{
            color: palette.background.alt,
            background: palette.primary.main,
            borderRadius: "3rem",
          }}
        >
          POST
        </Button>
      </FlexBetween>
    </WidgetWrapper>
  );
};

export default MyPostWidget;
