import {
  ManageAccountsOutlined,
  EditOutlined,
  WorkOutlinedOutlined,
  WorkoutLineOutlined,
} from "@mui/icons-material";
import { Box, Typography, Divider, useTheme } from "@mui/material";
import UserImage from "components/UserImage";
import FlexBetween from "components/FlexBetween";
import WidgetWrapper from "components/WidgetWrapper";
import { useSelector } from "react-redux";
import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

const UserWidget = ({ userId, picturePath }) => {
  const [user, setUser] = useState(null);
  const { palette } = useTheme();
  const navigate = useNavigate();
  const token = useSelector((state) => state.token);
  const dark = palette.neutral.dark;
  const medium = palette.neutral.medium;
  const main = palette.neutral.main;

  // call api to grab user information
  const getUser = async () => {
    const response = await fetch(`http://localhost:3001/users/${userId}`, {
      method: "GET",
      headers: { Authorization: `Bearer ${token}` }, //in Middleware/auth, we specifiy this: if token STARTS with Bearer, THEN it is authorized
    });
    const data = await response.json();
    setUser(data);
  };

  useEffect(() => {
    getUser();
  }, []); //eslint-disable-line react-hooks/exhaustive-deps

  if (!user) {
    return null;
  }

  const {
    firstName,
    lastName,
    location,
    occupation,
    viewedProfile,
    impressions,
    friends,
  } = user;

  return (
    <WidgetWrapper>
      {/* FIRST ROW */}
      <FlexBetween
        gap="0.5rem"
        pb="1.1rem"
        onClick={() => navigate(`/profile/${userId}`)}
      >
        <FlexBetween gap="1rem">
          <UserImage image={picturePath} />
          <Box>
            <Typography
              variant="h4"
              color={dark}
              fontWeight="500"
              sx={{
                "&:hover": {
                  color: palette.primary.light,
                  cursor: "pointer",
                },
              }}
            >
              {firstName} {lastName}
            </Typography>
            <Typography color={medium}>
              {friends.length} {/* showing number of friends user has */}
            </Typography>
            <ManangeAccountsOutlined />
          </Box>

          <Divider />

          {/* SECOND ROW */}
          <Box p="1 rem 0">
            <Box display="flex" alignItems="center" gap="1rem" mb="0.5rem">
              <LocationOutlined
                fontSize="large"
                color={main}
                // sx={{ color: main }}
              />
              <Typography color={medium}> {location} </Typography>
            </Box>

            <Box display="flex" alignItems="center" gap="1rem">
              <WorkoutLineOutlined
                fontSize="large"
                color={main}
                // sx={{ color: main }}
              />
              <Typography color={medium}> {location} </Typography>
            </Box>
          </Box>

          {/* THIRD ROW */}
          <Box p="1rem 0">
            <Flexbetween mb="0.5rem">
              <Typography color={medium}>Who's Viewed your Profile</Typography>
              <Typography color={main} fontWeight="500">
                {viewProfile}
              </Typography>
            </Flexbetween>
            <Flexbetween>
              <Typography color={medium}>Impressions of your Post</Typography>
              <Typography color={main} fontWeight="500">
                {impressions}
              </Typography>
            </Flexbetween>
          </Box>

          {/* FOURTH ROW */}
          <Box p="1rem 0">
            <Typography fontSize="1rem" color={main} fontWeight="500" mb="1rem">
              Social Profiles
            </Typography>

            <Flexbetween gap="1rem" mb="0.5rem">
              <Flexbetween gap="1rem">
                <img src="../assets/twitter.png" alt="twitter" />
                <Box>
                  <Typography color={main} fontWeight="500">
                    Twitter
                  </Typography>
                  <Typography color={medium}> Network </Typography>
                </Box>
              </Flexbetween>
              <EditOutlined sx={{ color: main }} />
            </Flexbetween>

            <Flexbetween gap="1rem">
              <Flexbetween gap="1rem">
                <img src="../assets/linkedin.png" alt="twitter" />
                <Box>
                  <Typography color={main} fontWeight="500">
                    LinkedIn
                  </Typography>
                  <Typography color={medium}> Network Platform </Typography>
                </Box>
              </Flexbetween>
              <EditOutlined sx={{ color: main }} />
            </Flexbetween>
          </Box>
        </FlexBetween>
      </FlexBetween>
    </WidgetWrapper>
  );
};

export default UserWidget;
